(307, 1, '2014-04-14 23:58:14', '2014-04-14 15:58:14', '<a title="pageload-ajax" href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" target="_blank">pageload-ajax</a> : 一个jquery分页加载插件,依赖jQuery,Underscore\r\n<h3>配置参数列表</h3>\r\n<code lang="javascript">\r\nvar defaults = { //默认参数\r\nurl : "url", //请求数据链接\r\npage : 5, //页码共显示页数\r\nrows : 4, //每页显示数\r\npageContPosi : $("#pageContPosi"), //数据容器\r\npageItemPosi : $("#pageItemPosi"), //页码容器\r\npageContCont : $("#pageContCont"), //数据模板,将数据填充进这里\r\npageNaviCont : { //页码模板,不需要结构更改不需要重写\r\n$prev : $(''<a class="prev disabled" role="prev" href="javascript:;"><b>&lt;</b></a>''), //上一页\r\n$next : $(''<a class="next" role="next" href="javascript:;"><b>&gt;</b></a>''), //下一页\r\n$dott : $(''<span class="dotted">...</span>''), //页码分隔符\r\nspageItem : ''<a class="page-item" role="page-item" href="javascript:;">0</a>'', //数字单项\r\nspage : "&lt;% _.each(pagenum, function(num) { %&gt;" + ''<a class="page-item" role="page-item" href="javascript:;">&lt;%= num %&gt;</a>''+ "&lt;% }); %&gt;" //分页数字模板\r\n},\r\nurlArgu : {}, //是否混合其它参数\r\nloading : $.noop, //Ajax请求前加载回调\r\nsuccess : $.noop, //Ajax请求成功回调\r\ndoneCallback : $.noop, //请求成功后附加数据处理，内已处理分页和页面数据\r\nfailCallback : $.noop, //Ajax请求失败回调\r\nnodataCallback:$.noop, //Ajax请求无结果回调\r\nclickPBeforeCB:$.noop, //点击页码时回调,如果点击页码时需要混合特殊参数时需要\r\n};\r\n</code>\r\n<h3>使用方法</h3>\r\n<code lang="html">\r\n<!-- ##### 页面数据 { ##### --></code>\r\n<div role="data"></div>\r\n<!-- ##### 页面数据 } ##### -->\r\n\r\n<!-- ##### 分页 { ##### -->\r\n<div class="page-wrap"></div>\r\n<!--  ##### 分页 } #####  -->\r\n\r\n<!-- ##### 数据模板 { ##### -->\r\n<script id="wrapMapDataTmpl" type="text/tmpl">\r\n\r\n        <% _.each(data,function(item){ %>\r\n\r\n	<li class="house-list-thumb clearfix">\r\n                 <span class="hx last"><%= item.houseTitle %></span></li>\r\n\r\n        <% }); %>   \r\n\r\n</script>\r\n<!-- ##### 数据模板 { ##### -->\r\n\r\n<!-- ##### 引入依赖 { ##### -->\r\n<script type="text/javascript" src="js/jquery.js"></script>\r\n<script type="text/javascript" src="js/underscore.js"></script>\r\n<script type="text/javascript" src="js/pageload-ajax.js"></script>\r\n <!-- ##### 引入依赖 } ##### -->\r\n\r\n<!-- ##### 实例化 { ##### -->\r\n<script type="text/javascript">\r\n        //初始化分页及数据\r\n        var newPage = new $.fn.page({ \r\n            url: "data/data.json", \r\n            page: 5,         //页码显示页数\r\n            rows: 5,         //每页显示数 \r\n            pageContPosi: $("div[role=''data'']"),   //数据容器\r\n            pageItemPosi: $("div[role=''page'']"),   //页码容器\r\n            pageContCont: $("#wrapMapDataTmpl"),\r\n            urlArgu: {},  //是否混合其它参数请求数据\r\n            doneCallback: function(dataGet){        //请求成功附加处理，内已处理分页和页面数据 \r\n            },\r\n            failCallback: function(){\r\n                alert("Server Error");\r\n            },\r\n            nodataCallback: function(){},\r\n            clickPBeforeCB: function(){}\r\n        });\r\n\r\n</script>\r\n<!-- ##### 实例化 } ##### -->\r\n</code>\r\n<a title="pageload-ajax" href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" target="_blank">pls visit demo</a>', 'jquery ajax分页加载插件', '', 'inherit', 'open', 'open', '', '290-revision-v1', '', '', '2014-04-14 23:58:14', '2014-04-14 15:58:14', '', 290, 'http://www.joecora.com/?p=307', 0, 'revision', '', 0),
(305, 1, '2014-04-14 17:59:41', '2014-04-14 09:59:41', '<a title="pageload-ajax" href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" target="_blank">pageload-ajax</a> : 一个jquery分页加载插件,依赖jQuery,Underscore\r\n<h3>配置参数列表</h3>\r\n<code lang="javascript">\r\nvar defaults = { //默认参数\r\nurl : "url", //请求数据链接\r\npage : 5, //页码共显示页数\r\nrows : 4, //每页显示数\r\npageContPosi : $("#pageContPosi"), //数据容器\r\npageItemPosi : $("#pageItemPosi"), //页码容器\r\npageContCont : $("#pageContCont"), //数据模板,将数据填充进这里\r\npageNaviCont : { //页码模板,不需要结构更改不需要重写\r\n$prev : $(''<a class="prev disabled" role="prev" href="javascript:;"><b>&lt;</b></a>''), //上一页\r\n$next : $(''<a class="next" role="next" href="javascript:;"><b>&gt;</b></a>''), //下一页\r\n$dott : $(''<span class="dotted">...</span>''), //页码分隔符\r\nspageItem : ''<a class="page-item" role="page-item" href="javascript:;">0</a>'', //数字单项\r\nspage : "&lt;% _.each(pagenum, function(num) { %&gt;" + ''<a class="page-item" role="page-item" href="javascript:;">&lt;%= num %&gt;</a>''+ "&lt;% }); %&gt;" //分页数字模板\r\n},\r\nurlArgu : {}, //是否混合其它参数\r\nloading : $.noop, //Ajax请求前加载回调\r\nsuccess : $.noop, //Ajax请求成功回调\r\ndoneCallback : $.noop, //请求成功后附加数据处理，内已处理分页和页面数据\r\nfailCallback : $.noop, //Ajax请求失败回调\r\nnodataCallback:$.noop, //Ajax请求无结果回调\r\nclickPBeforeCB:$.noop, //点击页码时回调,如果点击页码时需要混合特殊参数时需要\r\n};\r\n</code>\r\n<h3>使用方法</h3>\r\n<code lang="html">\r\n<!-- ##### 页面数据 { ##### --></code>\r\n<div role="data"></div>\r\n<!-- ##### 页面数据 } ##### -->\r\n\r\n<!-- ##### 分页 { ##### -->\r\n<div class="page-wrap"></div>\r\n<!--  ##### 分页 } #####  -->\r\n\r\n<!-- ##### 数据模板 { ##### -->\r\n<script id="wrapMapDataTmpl" type="text/tmpl">// <![CDATA[\r\n\r\n        <% _.each(data,function(item){ %>\r\n\r\n	<li class="house-list-thumb clearfix">\r\n                 <span class="hx last"><%= item.houseTitle %></span></li>\r\n\r\n        <% }); %>   \r\n\r\n// ]]></script>\r\n<!-- ##### 数据模板 { ##### -->\r\n\r\n<!-- ##### 引入依赖 { ##### -->\r\n<script type="text/javascript" src="js/jquery.js"></script>\r\n<script type="text/javascript" src="js/underscore.js"></script>\r\n<script type="text/javascript" src="js/pageload-ajax.js"></script>\r\n <!-- ##### 引入依赖 } ##### -->\r\n\r\n<!-- ##### 实例化 { ##### -->\r\n<script type="text/javascript">// <![CDATA[\r\n        //初始化分页及数据\r\n        var newPage = new $.fn.page({ \r\n            url: "data/data.json", \r\n            page: 5,         //页码显示页数\r\n            rows: 5,         //每页显示数 \r\n            pageContPosi: $("div[role=''data'']"),   //数据容器\r\n            pageItemPosi: $("div[role=''page'']"),   //页码容器\r\n            pageContCont: $("#wrapMapDataTmpl"),\r\n            urlArgu: {},  //是否混合其它参数请求数据\r\n            doneCallback: function(dataGet){        //请求成功附加处理，内已处理分页和页面数据 \r\n            },\r\n            failCallback: function(){\r\n                alert("Server Error");\r\n            },\r\n            nodataCallback: function(){},\r\n            clickPBeforeCB: function(){}\r\n        });\r\n\r\n// ]]></script>\r\n<!-- ##### 实例化 } ##### -->\r\n\r\n<a title="pageload-ajax" href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" target="_blank">pls visit demo</a>', 'jquery ajax分页加载插件', '', 'inherit', 'open', 'open', '', '290-revision-v1', '', '', '2014-04-14 17:59:41', '2014-04-14 09:59:41', '', 290, 'http://www.joecora.com/?p=305', 0, 'revision', '', 0),
(291, 1, '2014-04-14 15:50:08', '2014-04-14 07:50:08', '<a title="pageload-ajax" href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" target="_blank">pageload-ajax</a> : 一个jquery分页加载插件,依赖jQuery,Underscore\r\n\r\n<h3>配置参数列表</h3>\r\n<code lang="javascript">\r\n var defaults = {            //默认参数     \r\n            url          : "url",       //请求数据链接\r\n            page         : 5,         //页码共显示页数\r\n            rows         : 4,         //每页显示数 \r\n            pageContPosi : $("#pageContPosi"),  //数据容器  \r\n            pageItemPosi : $("#pageItemPosi"),  //页码容器\r\n            pageContCont : $("#pageContCont"),  //数据模板,将数据填充进这里\r\n            pageNaviCont : {                    //页码模板,不需要结构更改不需要重写\r\n                $prev : $(''<a class="prev disabled" role="prev" href="javascript:;"><b>&lt;</b></a>''),  //上一页\r\n                $next : $(''<a class="next" role="next" href="javascript:;"><b>&gt;</b></a>''),           //下一页\r\n                $dott : $(''<span class="dotted">...</span>''),                                           //页码分隔符               \r\n                spageItem : ''<a class="page-item" role="page-item" href="javascript:;">0</a>'',          //数字单项\r\n                spage : "<% _.each(pagenum, function(num) { %>" + ''<a class="page-item" role="page-item" href="javascript:;"><%= num %></a>''+ "<% }); %>"               //分页数字模板\r\n            },        \r\n            urlArgu      : {},        //是否混合其它参数\r\n            loading      : $.noop,  //Ajax请求前加载回调\r\n            success      : $.noop,  //Ajax请求成功回调\r\n            doneCallback : $.noop,    //请求成功后附加数据处理，内已处理分页和页面数据 \r\n            failCallback : $.noop,  //Ajax请求失败回调\r\n            nodataCallback:$.noop,  //Ajax请求无结果回调\r\n            clickPBeforeCB:$.noop,  //点击页码时回调,如果点击页码时需要混合特殊参数时需要\r\n        };\r\n</code>\r\n\r\n<h3>使用方法</h3>\r\n<code lang="html">\r\n<!-- ##### 页面数据 { ##### -->\r\n    <div role=''data''>\r\n\r\n    </div>\r\n    <!-- ##### 页面数据 } ##### -->\r\n\r\n    <!-- ##### 分页 { ##### -->\r\n    <div class="page-wrap">\r\n        <div class="pagination" role="page">\r\n\r\n        </div>\r\n    </div>\r\n    <!--  ##### 分页 } #####  -->  \r\n\r\n    <!-- ##### 数据模板 { ##### -->\r\n    <script type="text/tmpl" id="wrapMapDataTmpl">  \r\n        <% _.each(data,function(item){ %>\r\n            <li class="house-list-thumb clearfix">\r\n                 <span class="hx last"><%= item.houseTitle %></span>\r\n            </li>\r\n        <% }); %>   \r\n    </script>   \r\n    <!-- ##### 数据模板 { ##### -->\r\n\r\n    <!-- ##### 引入依赖 { ##### -->\r\n    <script src="js/jquery.js"></script>\r\n    <script src="js/underscore.js"></script>    \r\n    <script src="js/pageload-ajax.js"></script>\r\n    <!-- ##### 引入依赖 } ##### -->\r\n\r\n    <!-- ##### 实例化 { ##### -->\r\n    <script>\r\n        //初始化分页及数据\r\n        var newPage = new $.fn.page({ \r\n            url: "data/data.json", \r\n            page: 5,         //页码显示页数\r\n            rows: 5,         //每页显示数 \r\n            pageContPosi: $("div[role=''data'']"),   //数据容器\r\n            pageItemPosi: $("div[role=''page'']"),   //页码容器\r\n            pageContCont: $("#wrapMapDataTmpl"),\r\n            urlArgu: {},  //是否混合其它参数请求数据\r\n            doneCallback: function(dataGet){        //请求成功附加处理，内已处理分页和页面数据 \r\n            },\r\n            failCallback: function(){\r\n                alert("Server Error");\r\n            },\r\n            nodataCallback: function(){},\r\n            clickPBeforeCB: function(){}\r\n        });\r\n    </script>\r\n    <!-- ##### 实例化 } ##### -->\r\n</code>\r\n\r\n<a href="https://github.com/CoraJoe/PluginsByJoe/tree/master/pageload-ajax" title="pageload-ajax" target="_blank">pls visit demo</a>', 'jquery ajax分页加载插件', '', 'inherit', 'open', 'open', '', '290-revision-v1', '', '', '2014-04-14 15:50:08', '2014-04-14 07:50:08', '', 290, 'http://www.joecora.com/?p=291', 0, 'revision', '', 0);
